import './AdminLogin.css';
import React, { Component } from 'react';
import { Redirect } from 'react-router-dom';


class AdminLogin extends Component {
    constructor(props){
        super(props);
        const token = localStorage.getItem("token");

        let loggedIn = true
        if(token == null){
            loggedIn = false
        }
        this.state ={
            username: '',
            password: '',
            loggedIn
        }
        this.onChange = this.onChange.bind(this)
        this.summitForm = this.summitForm.bind(this)
    }

    summitForm(e){
        e.preventDefault();
        const { username, password} = this.state
        console.log(username, password)
        //login magic
        // if(username ==="abc" && password==="1"){
        //     localStorage.setItem('token', 'adsajajkalsklkasda')
        //     this.setState ={
        //         loggedIn: true   
        //     }
        // }
    }

    onChange(e){
        this.setState({
            [e.target.name]: e.target.value
        })
    }
    render() {
        // if(this.state.loggedIn){
        //     return <Redirect to='/admin-manager'/>
        // }
        return (
            <div className="bg">
                <h3 style={{color:'white'}}>ADMIN LOGIN</h3>
                <form className="form">
                    <div className="form-group">
                        <input className="form-control" name="username" value={this.state.username} onChange={this.onChange} placeholder="Admin"/>
                    </div>
                    <div className="form-group">
                        <input type="password" className="form-control" name="password" value={this.state.password} onChange={this.onChange} placeholder="Password"/>
                    </div>
                    <button type="submit" className="btn btn-primary btn-block btn-large" onClick={this.summitForm}>Login</button>
                </form>
            </div>
        );
    }
}

export default AdminLogin;
