import { Container, Dropdown, DropdownItem, DropdownMenu, DropdownToggle, Table } from "reactstrap";
import './CompanyList.css';
import logo from './../../images/logo.png';
import { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import Panigations from "../Panigation/Panigation";
import PostList from "../PostList/PostList";


function CompanyList(){
    const [filter, setFilter] = useState(false);
    const toggle = () => setFilter(prevState => ! prevState);
    const [searchTerm, setSearchTerm] = useState("");
    const [searchResult, setSearchResult] = useState([]);
    const [panigation, setPanigation] = useState({
        page:1,
        limit: 5,
        totalRows: 6,
    });

    const [postList, setPostList] = useState([]);
    useEffect(()=>{
        async function fetchPostList(){
            const requestURL = 'https://reqres.in/api/users?page=1';
            const response = await fetch(requestURL);
            const responseJSON = await response.json();
            console.log({responseJSON});

            const {data} = responseJSON;
            setPostList(data);
        }
        fetchPostList();
    },[])

    const handleChange = event =>{
        setSearchTerm(event.target.value);
    }

    // useEffect(() => {
    //     const results = data.filter(newdata =>
    //          newdata.includes(searchTerm));
    //     setSearchResult(results);
    // }, [searchTerm]);

    function handlePageChange(newPage){
        console.log(newPage);
    }


    return(
        <div>
            <Container>
                <div>
                    <img src={logo} alt=""/>
                </div>
                <div className="banner">
                    Company List
                </div>
                <div className="search_form">
                    <span>
                        <input type="search" className="form-control rounded" placeholder="Search by name company" value={searchTerm} onChange={handleChange}/>
                        <Dropdown isOpen={filter}toggle={toggle}>
                            <DropdownToggle caret>
                                Filter
                            </DropdownToggle>
                            <DropdownMenu>
                                <DropdownItem>Active</DropdownItem>
                                <DropdownItem>Deactive</DropdownItem>
                            </DropdownMenu>
                        </Dropdown>
                    </span>
                </div>
            </Container>
            <Table className="table_css">
                    <thead>
                        <tr>
                            <th>STT</th>
                            <th>Company Name</th>
                            <th>Aplicant Name</th>
                            <th>Date Created</th>
                            <th>Status</th>
                            <th>Phone Number</th>
                            <th>Email Address</th>
                            <th>Edits/Details</th>
                        </tr>
                    </thead>
                    <tbody>
                        <PostList/>
                    </tbody>    
            </Table>
            <Panigations
                panigation={panigation}
                onPageChange={handlePageChange}
            />
        </div>
    );
}
export default CompanyList;
