import { Container, Form, FormGroup, Label, Row, Col, Table } from "reactstrap";
import Header from "../Nav/Header";
import "../../css/Dashboard.css";
import Sidebar from "../Sidebar/Sidebar";
import { Link, useHistory } from "react-router-dom";
import { useEffect, useState } from "react";
import notsigned from "../../images/status.png";
import doneinvoice from "../../images/invoicecompleted.png";
import done from "../../images/true.png";
import invoiceAPI from "../../api/invoiceAPI";
import { getUser } from "../../utils/Common";
import contractAPI from "../../api/contractAPI";
import ScaleLoader from "react-spinners/ScaleLoader";
import moment from "moment";
import notiAPI from "../../api/notiAPI";

function Dashboard() {
  const history = useHistory();
  const [listAllInvoice, setListAllInvoice] = useState([]);
  const [listAllContract, setListAllContract] = useState([]);
  const [listContractById, setListContractById] = useState([]);
  const [listInvoiceById, setListInvoiceById] = useState([]);
  const [noti, setNoti] = useState([]);
  const [listContract, setListContract] = useState([]);
  const [listInvoice, setListInvoice] = useState([]);
  const [currentPage] = useState(1);
  const [postPerPage] = useState(5);
  const indexOfLastPost = currentPage * postPerPage;
  const indexOfFirstPost = indexOfLastPost - postPerPage;
  const currentPostsContract = listContract.slice(
    indexOfFirstPost,
    indexOfLastPost
  );
  const currentPostsInvoice = listInvoice.slice(
    indexOfFirstPost,
    indexOfLastPost
  );
  let [loadingInvoice, setLoadingInvoice] = useState(true);
  let [loadingContract, setLoadingContract] = useState(true);
  var listNoti = noti
    .sort((a, b) => {
      return (
        new Date(a.createdDate).getTime() - new Date(b.createdDate).getTime()
      );
    })
    .reverse();
  const currentPostsNoti = listNoti.slice(indexOfFirstPost, indexOfLastPost);
  useEffect(() => {
    async function fetListNoti() {
      try {
        const res = await notiAPI.getAll();
        setNoti(res.data);
      } catch (error) {
        console.log(error);
      }
    }
    fetListNoti();
  }, []);

  useEffect(() => {
    async function getListDocument() {
      try {
        if (getUser().Role === "1") {
          await contractAPI
            .getContractByCompanyId(getUser().CompanyId)
            .then(function (contract) {
              invoiceAPI
                .getInvoiceByCompanyId(getUser().CompanyId)
                .then(function (invoice) {
                  setListInvoice(invoice.data);
                  setListContract(contract.data);
                })
                .catch(function (error) {
                  console.log(error);
                });
            })
            .catch(function (error) {
              console.log(error);
            });
        } else {
          await invoiceAPI
            .getInvoiceByViewerId(getUser().Id)
            .then(function (res1) {
              invoiceAPI
                .getInvoiceBySignerId(getUser().Id)
                .then(function (res2) {
                  contractAPI
                    .getContractByViewerId(getUser().Id)
                    .then(function (res3) {
                      contractAPI
                        .getContractBySignerId(getUser().Id)
                        .then(function (res4) {
                          const listInvoice1 = [...res1.data, ...res2.data];
                          const listContract1 = [...res3.data, ...res4.data];
                          setListInvoice(listInvoice1);
                          setListContract(listContract1);
                        })
                        .catch(function (error) {
                          console.log(error);
                        });
                    })
                    .catch(function (error) {
                      console.log(error);
                    });
                })
                .catch(function (error) {
                  console.log(error);
                });
            })
            .catch(function (error) {
              console.log(error);
            });
        }
      } catch (error) {
        console.log(error);
      }
    }
    getListDocument();
    setTimeout(() => {
      setLoadingContract(false);
    }, 2000);
    setTimeout(() => {
      setLoadingInvoice(false);
    }, 2000);
  }, []);

  useEffect(() => {
    async function getAllContrac() {
      try {
        await contractAPI
          .getContractByCompanyId(getUser().CompanyId)
          .then(function (res) {
            setListAllContract(res.data);
          });
      } catch (error) {
        console.log(error);
      }
    }
    setTimeout(() => {
      setLoadingContract(false);
    }, 1000);
    getAllContrac();
  }, []);

  useEffect(() => {
    async function getAllInvoice() {
      try {
        await invoiceAPI
          .getInvoiceByCompanyId(getUser().CompanyId)
          .then(function (res) {
            setListAllInvoice(res.data);
          });
      } catch (error) {
        console.log(error);
      }
    }
    setTimeout(() => {
      setLoadingInvoice(false);
    }, 1000);
    getAllInvoice();
  }, []);

  // useEffect(() => {
  //   async function getInvoiceById() {
  //     try {
  //       await invoiceAPI
  //         .getInvoiceByViewerId(getUser().Id)
  //         .then(function (res) {
  //           invoiceAPI.getInvoiceBySignerId(getUser().Id).then(function (res2) {
  //             const list = [...res.data, ...res2.data];
  //             setListInvoiceById(list);
  //           });
  //         });
  //     } catch (error) {
  //       console.log(error);
  //     }
  //   }
  //   getInvoiceById();
  // }, [getUser().Id]);

