import logo from './logo.png'
import './AdminLogin.css';
import React, { Component } from 'react';
import { Redirect } from 'react-router-dom';


class AdminLogin extends Component {
    constructor(props){
        super(props);
        let loggedIn = false
        this.state ={
            username: '',
            password: '',
            loggedIn
        }
        this.onChange = this.onChange.bind(this)
        this.summitForm = this.summitForm.bind(this)
    }

    onChange(e){
        this.setState({
            [e.target.name]: e.target.value
        })
    }

    summitForm(e){
            e.preventDefault();
            const { username, password} = this.state
            console.log(username, password)
            //login magic
            if(username==="A" && password==="1"){
                localStorage.setItem('token', 'adsajajkalsklkasdaqw23as')
                this.setState({
                    loggedIn: true
                })
            }else{
                alert('Incorect username or password')
            }

        }
    render() {
        if(this.state.loggedIn){
            return <Redirect to='/admin-manager'/>
        }
        return (
            <div className="bg">
                <img src={logo} alt=""/>
                <h3>ADMIN LOGIN</h3>
                <form className="form">
                    <div className="form-group">
                        <input className="form-control" name="username" value={this.state.username} onChange={this.onChange} placeholder="Admin"/>
                    </div>
                    <div className="form-group">
                        <input type="password" className="form-control" name="password" value={this.state.password} onChange={this.onChange} placeholder="Password"/>
                    </div>
                    <button type="submit" className="btn btn-primary btn-block btn-large" onClick={this.summitForm}>Login</button>
                </form>
            </div>
        );
    }
}

export default AdminLogin;
