import { Container, Input, Row, Table } from "reactstrap";
import Header from "../Nav/Header";
import Sidebar from "../Sidebar/Sidebar";
import Button from "@material-ui/core/Button";
import "../../css/Notification.css";
import { Link } from "react-router-dom";
import { useEffect, useState } from "react";
import notiAPI from "../../api/notiAPI";
import moment from "moment";

function Notification() {
  const [all, setAll] = useState([]);
  useEffect(() => {
    async function fetListNoti() {
      try {
        const res = await notiAPI.getAll();
        setAll(res.data);
      } catch (error) {
        console.log(error);
      }
    }
    fetListNoti();
  }, []);

  return (
    <div>
      <Sidebar />
      <div className="main-panel">
        <Header />
        <Container fluid={true}>
          <Row>
            <div>
              <p style={{ float: "left", fontSize: "18px", marginTop: "20px", marginLeft:10}}>
                Notification management
              </p>
              <Button
                style={{ position: "absolute", right: 0, marginTop: "20px" }}
                color="primary"
              >
                Mark all announcements as read
              </Button>
            </div>
          </Row>
          <Row>
            <Input style={{ width: 160 }} type="select" name="filter-type">
              <option>-Filter by type-</option>
              <option>2</option>
              <option>3</option>
              <option>4</option>
              <option>5</option>
            </Input>
            <Input style={{ width: 160, marginLeft:10 }} type="select" name="filter-status">
              <option>-Filter by status-</option>
              <option>2</option>
              <option>3</option>
              <option>4</option>
              <option>5</option>
            </Input>
          </Row>
          
          <Table>
            <tbody>
              {all.map((row) => (
                <tr key={row.id}>
                  <td>
                    <Row style={{fontWeight:'bold'}}>{row.content}</Row>
                    <Row style={{marginLeft:10}}>{row.title}</Row>
                    <Row style={{marginLeft:10}}>
                        <Link to="/">View detail</Link>
                    </Row>
                  </td>
                  <td style={{ position: "absolute", right: 10 }}>
                    {moment(row.createdDate).format("DD/MM/YYYY HH:mm:ss")}
                  </td>
                </tr>
              ))}
            </tbody>
          </Table>
        </Container>
      </div>
    </div>
  );
}
export default Notification;
