import { Container, Dropdown, DropdownItem, DropdownMenu, DropdownToggle, Table } from "reactstrap";
import './CompanyList.css';
import logo from './../../images/logo.png';
import { useEffect, useState } from "react";

function CompanyList(){
    const [filter, setFilter] = useState(false);
    const toggle = () => setFilter(prevState => ! prevState);
    const [searchTerm, setSearchTerm] = useState('');
    const [searchResult, setSearchResult] = useState([]);

    const data = [
        {stt:'1',cname:'Duc company', apname:'Duc Dep Trai', date:'2021-01-19', status:'active', phone:'0982515705', email:'duc@gmail.com'},
        {stt:'1',cname:'Duc company', apname:'Duc Dep Trai', date:'2021-01-19', status:'active', phone:'0982515705', email:'duc@gmail.com'},
        {stt:'1',cname:'Duc company', apname:'Duc Dep Trai', date:'2021-01-19', status:'active', phone:'0982515705', email:'duc@gmail.com'},
        {stt:'1',cname:'Duc company', apname:'Duc Dep Trai', date:'2021-01-19', status:'active', phone:'0982515705', email:'duc@gmail.com'}
    ];
    const handleChange = event =>{
        setSearchTerm(event.target.value);
    }

    useEffect(() => {
        const results = data.filter(newdata => newdata.toLowerCase().include(searchTerm));
        setSearchResult(results);
    }, [searchTerm]);

    return(
        <div>
            <Container>
                <div>
                    <img src={logo} alt=""/>
                </div>
                <div className="banner">
                    Company List
                </div>
                <div className="search_form">
                    <input type="search" className="form-control rounded" placeholder="Search by name company" value={searchTerm} onChange={handleChange}/>
                    <Dropdown isOpen={filter}toggle={toggle}>
                        <DropdownToggle caret>
                            Filter
                        </DropdownToggle>
                        <DropdownMenu>
                            <DropdownItem>Active</DropdownItem>
                            <DropdownItem>Deactive</DropdownItem>
                        </DropdownMenu>
                    </Dropdown>
                    </div>
            </Container>
            <Table className="table_css">
                    <thead>
                        <tr>
                        <th>STT</th>
                        <th>Company Name</th>
                        <th>Aplicant Name</th>
                        <th>Date Created</th>
                        <th>Status</th>
                        <th>Phone Number</th>
                        <th>Email Address</th>
                        <th>Edits/Details</th>
                        </tr>
                    </thead>
                    <tbody>
                        <tr>
                        <th scope="row">1</th>
                        <td>Mark</td>
                        <td>Otto</td>
                        <td>@mdo</td>
                        </tr>
                    </tbody>    
            </Table>
        </div>
    );
}
export default CompanyList;
