import { useEffect, useState } from "react";
import { Button, Form, FormGroup, Input, Col, ButtonGroup, Table } from "reactstrap";
import userListAPI from "../../../api/userListAPI";
import Header from "../../Nav/Header";
import Panigation from "../../Panigation/Panigation";
import Sidebar from "../../Sidebar/Sidebar";
import './../../../css/UserManagement.css';

function UserManagement(){
    const [userList, setUserList] = useState([]);
    const [currentPage, setCurrentPage] = useState(1);
    const [postPerPage] = useState(10);
    const indexOfLastPost = currentPage * postPerPage;
    const indexOfFirstPost = indexOfLastPost - postPerPage;
    const currentPosts = userList.slice(indexOfFirstPost, indexOfLastPost);
    useEffect(()=>{
        async function fetchUserList(){
            try {
                const response = await userListAPI.getAll();
                setUserList(response.data);
            }catch (error) {
            console.log(error);
        }}
        fetchUserList();
    },[])
    function paginate(pageNumber){
        setCurrentPage(pageNumber);
    }
    return(
        <div>
            <Sidebar/>
            <div className="main-panel">
                <Header/>
                <div className="form-search">
                    <FormGroup row>
                        <Button style={{height:'80%'}} color="primary">+Add user</Button>
                        <Col>
                            <Input type="text" name="search" placeholder="Search by account name"/>
                        </Col>
                        <Col>
                            <ButtonGroup>
                                <Button outline color="primary">Active</Button>
                                <Button outline color="primary">Deactive</Button>
                            </ButtonGroup>
                            <div style={{marginLeft:'35%', marginTop:'20px'}}>
                                <Panigation
                                    currentPage= {currentPage}
                                    postsPerPage={postPerPage}
                                    totalPosts = {userList.length}
                                    paginate={paginate}
                                    />
                            </div>
                        </Col>
                    </FormGroup>
                </div>
                <Table hover>
                    <thead>
                        <tr>
                            <th>Account name</th>
                            <th>Department</th>
                            <th>Email</th>
                        </tr>
                    </thead>
                    <tbody>
                        {currentPosts.map(user =>(
                            <tr key={user.id}>
                                <td>{user.name}</td>
                                <td>{user.subDepartment}</td>
                                <td>{user.email}</td>
                            </tr>
                        ))}
                    </tbody>
                </Table>
            </div>
        </div>
    );
}

export default UserManagement;
