import { useEffect, useState } from "react";
import {Button, Container, Form, FormGroup, Input, Modal, ModalBody, ModalFooter, ModalHeader} from "reactstrap";
import logo from '../../images/logo.png';
import ValidatePhone from "../Validation/ValidatePhone";
import './ForgotPassword.css';

function ForgotPassword(){
    const [phone, setPhone] = useState('');
    function handleOnChange(e) {
        setPhone(e.target.value)
    }

    const [modal, setModal] = useState(false);
    const toggle = () => setModal(!modal);
    const [count, setCount] = useState(0);
    const [countInTimeOut, setCountInTimeOut] = useState(0);
    useEffect(()=>{
        setTimeout(()=>{
            setCountInTimeOut(count);
        },1000);
        setCount(500)
    },[]);

    return(
        <Container>
            <div>
                Count: {count}
                <b/>
                set Timeout count: {countInTimeOut}
            </div>
            <img src={logo} alt=""/>
            <Form className="forgot">
                <FormGroup>
                    <h3>Forgot Password?</h3>
                </FormGroup>
                <FormGroup className="style_input">
                    <Input type="tel" required="required" placeholder="Please enter phone number" onChange={handleOnChange}/>
                </FormGroup>
                <FormGroup className="btn_next">
                    <Button className="btn" color="primary" onClick={toggle}>Next</Button>
                </FormGroup>
            </Form>
            <Modal isOpen={modal} toggle={toggle}>
                <ModalHeader className="otp_banner">Verifying OTP</ModalHeader>
                <ModalBody>
                    <Form>
                        <FormGroup>
                            <span>Please enter the OTP sent to Phone number to retrieve your password</span>
                        </FormGroup>
                        <FormGroup>
                            <Input type="text" name="otp"/>
                        </FormGroup>
                    </Form>
                </ModalBody>
                <ModalFooter>
                    <Button color="primary" onClick={toggle}>Verifying</Button>{' '}
                    <Button color="secondary" onClick={toggle}>Cancel</Button>
                </ModalFooter>
            </Modal>
        </Container>
    );
}

export default ForgotPassword;
